import {useState, useEffect} from 'react';
import {projectFirestore, projectStorage} from '../firebase/config'

const useFirestore = (collection) => {
    const [docs, setDocs] = useState([]);
    console.log(docs)
    //se lanza cuando las dependencias cambian
    useEffect(() => {
        const unsub = projectStorage.collection(collection)
        .orderBy('createdAt', 'desc')
        //El order by puede ser como queramos en un futuro estría bien que fuera por likes
            .onSnapshot((snap) =>{
                let documents = []
                snap.forEach(doc =>{
                    //los tres puntos importan toda la informacion del doc
                    documents.push({...doc.data(), id: doc.id});
                })
                setDocs(documents)
        });
        return() => unsub();
        //unsub() permite que se un suscriba a la coleccion cuando ya no la usemos
        //un callback cada que cambia la funcion el onsnapshot
    }, [collection])

    return {docs};
}

export default useFirestore;
