        <div className="sliderr">
            {
                sliderArr.map((item, index)=>{
                    return(
                        <div key={index} className="slidee" style = {{transform:`translateX(${x}%)`}}>
                            {item}
                        </div>
                    )
                })
            }
            <button id="goLeft" onClick={goLeft}>
                Left
            </button>
            <button id="goRight" onClick={goRight}>
                Right
            </button>
        </div>
    );
}

export default Slider;
