import firebase from 'firebase/app'
import 'firebase/storage';
import 'firebase/firestore';

// Your web app's Firebase configuration
var firebaseConfig = {
    apiKey: "AIzaSyAAby8dAjXuErJDGE5ib4jo4QZJc3V8thg",
    authDomain: "webpage-art.firebaseapp.com",
    projectId: "webpage-art",
    storageBucket: "webpage-art.appspot.com",
    messagingSenderId: "309630043076",
    appId: "1:309630043076:web:f58fdcd22177cff8871a91"
};

firebase.firestore().settings({ experimentalForceLongPolling: true });
// Initialize Firebase
firebase.initializeApp(firebaseConfig);

const projectStorage = firebase.storage();
const projectFirestore = firebase.firestore();
const timestamp = firebase.firestore.FieldValue.serverTimestamp;

export { projectStorage, projectFirestore, timestamp};
